import axios from 'axios';
import DatasourceBase from './DatasourceBase';
import QueryBuilder from './QueryBuilder';

class AjaxDatasource extends DatasourceBase {
  constructor(options = {}) {
    let { ajax } = options;
    if (ajax && typeof ajax === 'object') {
      ajax = {
        ...AjaxDatasource.prototype.defaults.ajax,
        ...ajax,
      };
    }

    const mergedOptions = {
      ...AjaxDatasource.prototype.defaults,
      ...options,
      ajax,
    };

    super(mergedOptions);

    this._total = null;
    this._data = null;
    if (mergedOptions.queryBuilder instanceof QueryBuilder) {
      this.queryBuilder = mergedOptions.queryBuilder;
    } else if (mergedOptions.queryBuilder) {
      this.queryBuilder = new QueryBuilder(mergedOptions.queryBuilder);
    }
    this.ajax = mergedOptions.ajax;
  }

  /**
     * Performs an AJAX call.
     * @param {string} url Url to remote endpoint to ajax for.
     * @param {string} method The AJAX method.
     */
  _ajaxCall(url, method) {
    const actualMethod = method ? method.toLowerCase() : method;
    let data = {};
    let actualUrl = url;

    if (actualMethod === 'get') {
      actualUrl = `${actualUrl}?${this.queryBuilder.getQueryString(this)}`;
    } else {
      data = this.queryBuilder.getQueryObject(this);
    }
    const axiosOptions = this.ajax.options || {};
    return axios({
      ...axiosOptions,
      method: actualMethod,
      url: actualUrl,
      data,
    });
  }

  /**
     * Builds an AJAX handler function.
     * @return {Function} A callback function ajax which
     *                    performs the actual ajax operation
     */
  _buildAjaxHandler() {
    const ajaxHandler = {
      function: () => this.ajax.call(this, this.queryBuilder),
      string: () => {
        const url = this.ajax;
        return this._ajaxCall(url, 'get');
      },
      object: () => {
        const { url } = this.ajax;
        const method = this.ajax.method || 'get';
        return this._ajaxCall(url, method);
      },
    };
    const ajaxObjectType = typeof this.ajax;
    const actualAjaxHandler = ajaxHandler[ajaxObjectType];
    if (!actualAjaxHandler) {
      throw new Error(`Does not support provided ajax object of type ${ajaxObjectType}`);
    }
    return actualAjaxHandler;
  }

  /**
     * Overrides for DatasourceBase
     */

  /**
     * @inheritdoc
     */
  get total() {
    return this._total;
  }

  /**
     * @inheritdoc
     */
  get data() {
    return this._data;
  }

  /**
     * @inheritdoc
     */
  async _update() {
    const requestStartArgs = {
      sender: this,
      prevented: false,
      preventDefault() {
        requestStartArgs.prevented = true;
      },
    };
    this.emit('requeststart', requestStartArgs);
    if (requestStartArgs.prevented) {
      return null;
    }
    const ajaxHandler = this._buildAjaxHandler();
    const response = await ajaxHandler.call(this);
    this.emit('requestend', {
      sender: this,
      response,
    });
    // If not provided an object, use the default mapper
    const mapper = this.ajax.mapper || AjaxDatasource.prototype.defaults.ajax.mapper;
    const mappedObject = mapper.call(this, response);
    this._data = mappedObject.data;
    this._total = mappedObject.total;
    return response;
  }
}

AjaxDatasource.prototype.defaults = {
  queryBuilder: {},
  // ajax can be an object, function, or even a simple string
  ajax: {
    // Additional options for axios
    options: {},
    // Maps the response to return the data in an expected format
    mapper(response) {
      return {
        data: response.data,
        total: response.total,
      };
    },
  },
};

export default AjaxDatasource;

import DatasourceBase from './DatasourceBase';

class ArrayDatasource extends DatasourceBase {
  constructor(options = {}) {
    const mergedOptions = {
      ...ArrayDatasource.prototype.defaults,
      ...options,
    };
    super(mergedOptions);
    this._rawData = mergedOptions.data;
    this._search = mergedOptions.search;
    this._sort = mergedOptions.sort;
    this._data = null;
    this._processedData = null;
  }

  _getProcessedData() {
    let processedData = [...this._rawData];
    if (this.searchArguments) {
      processedData = this._search.call(this, processedData, this.searchArguments);
    } else if (this.searchText) {
      processedData = this._search.call(this, processedData, this.searchText);
    }
    if (this._sort && this.sortArguments) {
      processedData = this._sort.call(this, processedData, this.sortArguments);
    }
    return processedData;
  }

  /**
     * Overrides for DatasourceBase
     */

  /**
     * @inheritdoc
     */
  get total() {
    if (this._processedData) {
      return this._processedData.length;
    }
    if (this._rawData) {
      return this._rawData.length;
    }
    return 0;
  }

  /**
     * @inheritdoc
     */
  get data() {
    return this._data;
  }

  /**
     * @inheritdoc
     */
  async _update() {
    if (!this._rawData) {
      return null;
    }
    this._processedData = this._getProcessedData();
    this._data = this._processedData.slice(this.offset, this.offset + this.size);
    return this._data;
  }
}

ArrayDatasource.prototype.defaults = {
  data: null,
  search(data) {
    return data;
  },
  /* eslint-disable no-unused-vars */
  sort(data, sortArguments) {
    return data;
  },
  /* eslint-enable no-unused-vars */
};

export default ArrayDatasource;

import EventEmitter from 'events';

/**
 * DatasourceBase represents an abstract
 * class for derived datasource classes
 * to use to represent core collections
 * of data.
 *
 * It provides additional features such
 * as pagination, sorting, etc.
 */
class DatasourceBase extends EventEmitter {
  /**
     * Create a datasource.
     * @param {object} options - The datasource options.
     */
  constructor(options = {}) {
    super();
    const mergedOptions = {
      ...DatasourceBase.prototype.defaults,
      ...options,
    };
    this.offset = mergedOptions.offset;
    this.size = mergedOptions.size;
    this.searchText = mergedOptions.searchText;
    this.searchArguments = null;
    this.sortArguments = null;
    this.loading = false;

    if (mergedOptions.page) {
      this.page = mergedOptions.page;
    }
  }

  /**
     * Overridables
     */

  /**
     * Gets the current set of data the datasource
     * has, based on pagination, sorting, and filtering.
     * @return {null|Array} The array of data items.
     */
  get data() {
    throw new Error(`Getter not supported for ${this.constructor.name}`);
  }

  /**
     * Gets the total amount of data items available
     * for the datasource to page through.
     * @return {null|Array} The array of data items.
     */
  get total() {
    throw new Error(`Getter not supported for ${this.constructor.name}`);
  }

  /**
     * Gets the total amount of data items for the current page.
     * @return {null|Array} The array of data items.
     */
  get pageTotal() {
    if (this.offset === this.total) {
      return 0;
    }
    const itemsRemaining = this.total - this.offset;
    if (itemsRemaining <= this.size) {
      return itemsRemaining;
    }
    return this.size;
  }

  /**
    * Updates the datasource, using the currently
    * configured search text, sort settings, and
    * pagination options.
    * @return {Promise} A promise object, resolved when the update is completed.
    */
  _update() {
    throw new Error(`Method not supported for ${this.constructor.name}`);
  }

  /**
     * Getters & Setters
     */

  /**
     * Gets the current page the datasource is
     * currently set to.
     * @return {Number} The current page number.
     */
  get page() {
    if (this.totalPages === 0) {
      return 0;
    }
    return (this.offset + this.size) / this.size;
  }

  /**
     * @param  {Number} newPage - The new page to set to.
     */
  set page(newPage) {
    this.offset = newPage * this.size - this.size;
  }

  /**
     * Boolean to indicate whether it's the first page or not.
     * @return {boolean} Whether its the first page or not.
     */
  get isFirstPage() {
    if (this.page <= 1) {
      return true;
    }
    return false;
  }

  /**
     * Boolean to indicate whether it's the last page or not.
     * @return {boolean} Whether its the last page or not.
     */
  get isLastPage() {
    if (this.page >= this.totalPages) {
      return true;
    }
    return false;
  }

  /**
     * The total amount of pages
     * @return {null|Number} Null if total wasn't provided.
     *                       A number representing current page otherwise.
     */
  get totalPages() {
    if (this.total === null || !this.size) {
      return null;
    }
    if (this.total === 0) {
      return 0;
    }
    return Math.ceil(this.total / this.size);
  }

  /**
     * Methods
     */

  /**
    * Updates the datasource, using the currently
    * configured search text, sort settings, and
    * pagination options.
    * @return {Promise} A promise object, resolved when the update is completed.
    */
  async update() {
    this.loading = true;
    try {
      const updateStartArgs = {
        sender: this,
        prevented: false,
        preventDefault() {
          updateStartArgs.prevented = true;
        },
      };
      this.emit('updatestart', updateStartArgs);
      if (updateStartArgs.prevented) {
        return null;
      }
      const updatedResponse = await this._update();
      this.emit('updateend', {
        sender: this,
      });
      this.loading = false;
      return updatedResponse;
    } catch (ex) {
      this.loading = false;
      throw ex;
    }
  }

  /**
     * Traverse to the specified page, and update the datasource accordingly.
     * @param  {Number} pageNum - The new page to traverse to.
     * @return {Promise} A promise with a boolean data. True if
     *                   successfully traversed to specified page.
     *                   False otherwise.
     */
  async goToPage(pageNum) {
    if (pageNum > 0 && pageNum <= this.totalPages) {
      this.page = pageNum;
      await this.update();
      return true;
    }
    return false;
  }

  /**
     * Traverse to the next page, and update the datasource accordingly.
     * @return {Promise} A promise with a boolean data. True if
     *                   successfully traversed to next page.
     *                   False otherwise.
     */
  async nextPage() {
    return this.goToPage(this.page + 1);
  }

  /**
     * Traverse to the previous page, and update the datasource accordingly.
     * @return {Promise} A promise with a boolean data. True if
     *                   successfully traversed to previous page.
     *                   False otherwise.
     */
  async prevPage() {
    return this.goToPage(this.page - 1);
  }

  /**
     * Given a search text, filters the data accordingly.
     * @param  {string} searchArguments - The search text to filter with, or
     *                                    the search arguments object to filter
     *                                    with specific column filtering.
     * @return {Promise} A promise object, resolved when the search is completed.
     */
  async search(searchArguments) {
    this.searchText = null;
    this.searchArguments = null;
    if (typeof searchArguments === 'string') {
      this.searchText = searchArguments;
    } else {
      this.searchArguments = searchArguments;
    }
    this.offset = 0;
    return this.update();
  }

  /**
     * Sort the data based on a field and direction.
     * @param  {Object} sortArguments - Object with keys as the field names to sort.
     *                                  The values of the object is the sort direction.
     *                                  The sort direction can be 'asc', 'ascend', 'desc',
     *                                  'descending'
     * @return {Promise} A promise object, resolved when the sorting is completed.
     */
  async sort(sortArguments = null) {
    this.sortArguments = sortArguments;
    return this.update();
  }
}

DatasourceBase.prototype.defaults = {
  offset: 0,
  size: 10,
  searchText: null,
  searchArguments: null,
  sortArguments: null,
};

export default DatasourceBase;

import queryString from 'query-string';

function hasObjectInArray(arr) {
  for (let i = 0; i < arr.length; i += 1) {
    if (typeof arr[i] === 'object') {
      return true;
    }
  }
  return false;
}

/**
 * Using a datasource object, it builds
 * the data object for AJAX post requests,
 * or the query string for AJAX get requests
 *
 * @example <caption>With AJAX Datasource</caption>
 * let datasource = new AjaxDatasource({
 *   // ...
 *   queryBuilder: new QueryBuilder({
 *     // ...
 *   })
 * });
 * @example <caption>With Adding to Defaults</caption>
 * let datasource = new AjaxDatasource({
 *   // ...
 *   queryBuilder: new QueryBuilder({
 *     // ...
 *     serialiseToQueryObject(datasource) {
 *       // Calls the original default serialiser
 *       let queryObject = QueryBuilder.prototype
 *                         .defaults.serialiseToQueryObject(datasource);
 *       // ... Do your additional operations
 *     },
 *     serialiseToQueryString(objectToSerialise) {
 *       // Calls the original default serialiser
 *       let queryString = QueryBuilder.prototype
 *                         .defaults.serialiseToQueryString(objectToSerialise);
 *       // ... Do your additional operations
 *     },
 *     queryStringOptions: {
 *       ...QueryBuilder.prototype.defaults.queryStringOptions,
 *       // ... Put additional options here
 *       // For available options, check the docs for QueryBuilder constructor
 *     }
 *   })
 * });
 * @example <caption>With Extra Parameter for GET/POST Request</caption>
 * let datasource = new AjaxDatasource({
 *   // ...
 *   queryBuilder: new QueryBuilder({
 *     serialiseToQueryObject(datasource) {
 *       // Calls the original converter
 *       let queryObject = QueryBuilder.prototype.defaults.serialiseToQueryObject(datasource);
 *       queryObject['extraGreetingData'] = 'HelloWorld';
 *       return queryObject;
 *     },
 *   })
 * });
 */
class QueryBuilder {
  /**
   * Callback function that converts a datasource
   * into a query object (which in turn can be
   * converted to query string).
   *
   * @typedef {Function} QueryObjectConverterCallback
   * @param {DatasourceBase} datasource
   * The datasource to convert to a query object.
   * @returns {Object} The query object.
   */

  /**
   * Callback function which serialises the
   * query string object to the query string.
   * By default, it uses the `query-string` package
   * to convert the query string object to query
   * string.
   *
   * @typedef {Function} SerialiseToQueryStringCallback
   * @param {Object} objectToSerialise
   * The object to serialise to query string.
   * By default, it's the query object.
   * @returns {string} The query string.
   */

  /**
   * The query string options object that
   * contains configuration and settings
   * to affect the process of converting
   * the datasource to a query string.
   *
   * For a full list of available options
   * and settings, see {@link https://www.npmjs.com/package/query-string#stringifyobject-options}
   *
   * @typedef {Object} QueryStringOptions
   */

  /**
   * @param {Object} options The options object.
   * @param {QueryObjectConverterCallback} [options.serialiseToQueryObject]
   * Function which converts a datasource
   * into a query object (which in turn can
   * be converted to query string).
   * @param {QueryStringOptions} [options.queryStringOptions]
   * See {@link QueryStringOptions}
   * @param {SerialiseToQueryStringCallback} [options.serialiseToQueryString]
   * The function which serialises the
   * query string object to the query string.
   */
  constructor(options = {}) {
    const mergedOptions = {
      ...QueryBuilder.prototype.defaults,
      ...options,
    };
    /**
     * See {@link QueryObjectConverterCallback}
     *
     * @name QueryBuilder#serialiseToQueryObject
     * @access public
     * @type {QueryObjectConverterCallback}
    */
    this.serialiseToQueryObject = mergedOptions.serialiseToQueryObject;

    /**
     * See {@link QueryStringOptions}
     *
     * @name QueryBuilder#queryStringOptions
     * @access public
     * @type {QueryStringOptions}
    */
    this.queryStringOptions = mergedOptions.queryStringOptions;

    /**
     * If you need to change the behavior of the
     * query string, then you should first check
     * {@link QueryBuilder#queryStringOptions}
     *
     * For more information, see {@link SerialiseToQueryStringCallback}
     *
     * @name QueryBuilder#serialiseToQueryString
     * @access public
     * @type {SerialiseToQueryStringCallback}
    */
    this.serialiseToQueryString = mergedOptions.serialiseToQueryString;
  }

  /**
   * Serialises a datasource into
   * an object and returns that object.
   * That object is passed as data as
   * part of AJAX post requests.
   *
   * @param {DatasourceBase} datasource The datasource.
   * @access public
   * @returns {Object} The query object.
   */
  getQueryObject(datasource) {
    if (!this.serialiseToQueryObject) {
      throw new Error('serialiseToQueryObject must be specified and cannot be undefined or null.');
    }
    return this.serialiseToQueryObject.call(this, datasource);
  }

  /**
   * Serialises a datasource into
   * a query string and returns that string.
   * This string is passed as data as
   * part of AJAX get requests.
   *
   * @param {DatasourceBase} datasource The datasource.
   * @access public
   * @returns {string} The query string.
   */
  getQueryString(datasource) {
    const objectToSerialise = { ...this.getQueryObject(datasource) };
    if (this.serialiseToQueryString) {
      return this.serialiseToQueryString.call(this, objectToSerialise);
    }
    throw new Error('Query string serialiser is specified as null or undefined in the options.');
  }
}

QueryBuilder.prototype.defaults = {
  // Properties to include in the
  // serialisation to GET query string,
  // or when constructing the
  // data object.
  // By default, it only include
  // primitive properties, ignoring
  // objects, arrays and certain properties.
  serialiseToQueryObject(datasource) {
    const entries = Object.entries(datasource);
    const filteredEntries = entries.filter((entry) => {
      const key = entry[0];
      if (key.indexOf('_') === 0 || key === 'loading') { // Ignore 'private' properties and specified properties
        return false;
      }
      // Ignore properties that shouldn't be serialised,
      // i.e. properties that configures the serialisation
      // itself.
      const value = entry[1];
      if (value === null) { // Don't serialise null values
        return false;
      }
      if (key === 'queryBuilder' || key === 'ajax') {
        return false;
      }
      return true;
    });
    const queryObject = {
      // Computed getters doesn't get included in ES6 spread syntax,
      // so we manually include the necessary one below.
      page: datasource.page,
    };
    filteredEntries.forEach((entry) => {
      const key = entry[0];
      const value = entry[1];
      queryObject[key] = value;
    });
    return queryObject;
  },
  /* eslint-disable no-param-reassign */
  serialiseToQueryString(objectToSerialise) {
    Object.keys(objectToSerialise).forEach((prop) => {
      if (Array.isArray(objectToSerialise[prop]) && hasObjectInArray(objectToSerialise[prop])) {
        for (let i = 0; i < objectToSerialise[prop].length; i += 1) {
          objectToSerialise[prop][i] = JSON.stringify(objectToSerialise[prop][i]);
        }
      } else if (typeof objectToSerialise[prop] === 'object') {
        objectToSerialise[prop] = JSON.stringify(objectToSerialise[prop]);
      }
    });
    return queryString.stringify(objectToSerialise, this.queryStringOptions);
  },
  /* eslint-enable no-param-reassign */
  queryStringOptions: {
    // By default, uses NPM query
    // string package to serialise
    // to query string. For full list
    // of options, see:
    //   https://www.npmjs.com/package/query-string#stringifyobject-options
    // If you specified a query string serialiser,
    // this options can optionally be used.
    arrayFormat: 'bracket',
  },
};

export default QueryBuilder;

